INSERT INTO `ipqu_postmeta` VALUES (1590, 259, 'text', '<h2>Search fields include:</h2>\r\nKeywords, decision text searches, legal provisions, applicants, patentees and opponents.\r\n<h2>Search filters include:</h2>\r\n<dl>\r\n 	<dt>Appeal Type</dt>\r\n 	<dd>\r\n<ul>\r\n 	<li>Appeal following Examination</li>\r\n 	<li>Appeal following Opposition</li>\r\n</ul>\r\n</dd>\r\n</dl>\r\n<dl>\r\n 	<dt>Appeal Outcome</dt>\r\n 	<dd>Includes outcomes in more than 15 categories, such as:\r\n<ul>\r\n 	<li>Allowance of appeal</li>\r\n 	<li>Appeal deemed not to have been filled</li>\r\n 	<li>Appeal of the applicant was rejected</li>\r\n 	<li>Appeal of the applicant inadmissible</li>\r\n 	<li>Remittal for grant</li>\r\n 	<li>Continuation of examination procedure</li>\r\n</ul>\r\n</dd>\r\n</dl>');
