INSERT INTO `ipqu_postmeta` VALUES (2300, 331, 'member_1_text', 'Gianluca has spent his adult life working on business intelligence projects and creating data architectures for universities, government institutions and private companies across Europe and North America.\r\n\r\nHe’s a frequently cited researcher in the innovation analytics and innovation economics fields, as well as an European Patent Office (EPO) honoree for his work improving the data quality of the EPO raw data product offering.\r\n\r\nGianluca graduated with an engineering degree from Politecnico Milano.');
