INSERT INTO `ipqu_posts` VALUES (44, 1, '2018-05-02 08:53:00', '2018-05-02 08:53:00', 'A native of Sweden, mechanical engineer, European patent attorney and ice hockey fan, Tony has spent his entire professional life in the world of invention and innovation.\r\n\r\nThrough his career, he’s developed extensive commercial, legal and managerial expertise in the domain of global IP management. Tony has long incorporated innovation analytics into the advice and strategic plans he’s shared with executives, CTOs and corporate strategy leaders.\r\n\r\nHe graduated from the Royal Institute of Technology (KTH) with an engineering degree.', 'Tony Afram', '', 'publish', 'closed', 'closed', '', 'tony-afram', '', '', '2018-05-02 08:53:57', '2018-05-02 08:53:57', '', 0, 'https://localhost/ipquants/dist/?post_type=team&#038;p=44', 0, 'team', '', 0);
