INSERT INTO `ipqu_posts` VALUES (46, 1, '2018-05-02 08:54:23', '2018-05-02 08:54:23', 'Gianluca has spent his adult life working on business intelligence projects and creating data architectures for universities, government institutions and private companies across Europe and North America.\r\n\r\nHe’s a frequently cited researcher in the innovation analytics and innovation economics fields, as well as an European Patent Office (EPO) honoree for his work improving the data quality of the EPO raw data product offering.\r\n\r\nGianluca graduated with an engineering degree from Politecnico Milano.', 'Gianluca Tarasconi', '', 'publish', 'closed', 'closed', '', 'gianluca-tarasconi', '', '', '2018-05-02 08:54:23', '2018-05-02 08:54:23', '', 0, 'https://localhost/ipquants/dist/?post_type=team&#038;p=46', 0, 'team', '', 0);
