INSERT INTO `ipqu_posts` VALUES (80, 1, '2018-05-02 13:35:33', '2018-05-02 13:35:33', '<h2>Answers to the Most Important\r\nQuestions ASAP</h2>\r\n<div class=\"quote quote_2\">\r\n\r\n<i>“Tap into multiple public and proprietary sources in order to help companies answer tough questions about innovation and growth.\r\nThese can pertain to identifying adjacent opportunities (including novel uses of a company’s existing capabilities), finding white-space opportunities, monetizing a company’s own IP, identifying and screening acquisition targets and venture partners and gaining an understanding of new technologies that may represent a competitive threat.“</i>\r\n<div class=\"quote__author\">\r\n\r\nReport: Most Innovative Companies 2016\r\n\r\nBoston Consulting Group\r\n\r\n</div>\r\n</div>', 'Industries', '', 'trash', 'closed', 'closed', '', 'industries__trashed', '', '', '2018-11-08 16:11:45', '2018-11-08 16:11:45', '', 0, 'https://localhost/ipquants/dist/?page_id=80', 0, 'page', '', 0);
