INSERT INTO `ipqu_posts` VALUES (84, 1, '2018-05-02 13:36:24', '2018-05-02 13:36:24', '<h2>Answers to the Most Important\r\nQuestions ASAP</h2>\r\n<div class=\"quote quote_2\">\r\n\r\n<i>“Tap into multiple public and proprietary sources in order to help companies answer tough questions about innovation and growth.\r\nThese can pertain to identifying adjacent opportunities (including novel uses of a company’s existing capabilities), finding white-space opportunities, monetizing a company’s own IP, identifying and screening acquisition targets and venture partners and gaining an understanding of new technologies that may represent a competitive threat.“</i>\r\n<div class=\"quote__author\">\r\n\r\nReport: Most Innovative Companies 2016\r\n\r\nBoston Consulting Group\r\n\r\n</div>\r\n</div>', 'Industries', '', 'inherit', 'closed', 'closed', '', '80-revision-v1', '', '', '2018-05-02 13:36:24', '2018-05-02 13:36:24', '', 80, 'https://localhost/ipquants/dist/80-revision-v1/', 0, 'revision', '', 0);
INSERT INTO `ipqu_posts` VALUES (89, 1, '2018-05-02 13:44:59', '2018-05-02 13:44:59', '<strong>Titles:</strong> Director of Licensing; Director, Business Development; Senior Licensing Manager; Business Development Analyst; Contracts and Licensing Manager\r\n\r\n<strong>Use Cases:</strong>\r\n<ul>\r\n 	<li>Map relevant players (inventors, companies and universities) in any tech domain</li>\r\n 	<li>Understand strengths and weaknesses of players (inventors, companies and universities) in a specific tech field</li>\r\n 	<li>Identify people and organizations with partner potential</li>\r\n 	<li>Identify potential licensees or partners to monetize your IP</li>\r\n 	<li>Geographically map inventing activity</li>\r\n</ul>\r\n<strong>ipQuants Value:</strong>\r\n<div class=\"quote quote_3\">\r\n\r\n<i>“In our business, you have to be a curious guy. I can go into ipQuants and in minutes, I have a really good idea which academic institutions are strong, who the top inventors are and which companies are working in the same fields.”</i>\r\n<div class=\"quote__author\">\r\n\r\nChief IP Counsel\r\n\r\nMedTech Manufacturer\r\n\r\n</div>\r\n</div>', 'Technology Transfer', '', 'publish', 'closed', 'closed', '', 'technology-transfer', '', '', '2018-05-02 13:44:59', '2018-05-02 13:44:59', '', 0, 'https://localhost/ipquants/dist/?post_type=industries&#038;p=89', 0, 'industries', '', 0);
INSERT INTO `ipqu_posts` VALUES (90, 1, '2018-05-02 13:45:15', '2018-05-02 13:45:15', '<strong>Titles:</strong> CTO, Chief Scientific Officer, Head of R&amp;D, Advanced Engineering Manager\r\n\r\n<strong>Use Cases:</strong>\r\n<ul>\r\n 	<li>Get an understanding of relevant players (inventors, companies and universities) in any tech domain</li>\r\n 	<li>Identify relevant players with partner potential, including open innovation possibilities within a particular field</li>\r\n 	<li>Easily extract relevant patent data without a dedicated patent search tool</li>\r\n 	<li>Retrieve relevant CPC patent classes</li>\r\n 	<li>Generate Top 100 lists of most active patentees</li>\r\n 	<li>Prepare for innovation committee, strategy planning sessions and IP committee meetings</li>\r\n 	<li>Track promising start-ups</li>\r\n</ul>', 'Strategic Planning', '', 'publish', 'closed', 'closed', '', 'strategic-planning', '', '', '2018-05-02 13:45:15', '2018-05-02 13:45:15', '', 0, 'https://localhost/ipquants/dist/?post_type=industries&#038;p=90', 0, 'industries', '', 0);
INSERT INTO `ipqu_posts` VALUES (91, 1, '2018-05-02 13:45:29', '2018-05-02 13:45:29', '<strong>Titles:</strong> Chief People Officer, Chief Talent Officer, Director of Recruiting, Recruiter, Head of Sourcing\r\n\r\n<strong>Use Cases:</strong>\r\n<ul>\r\n 	<li>Quickly identify top innovators by technology category</li>\r\n 	<li>Identify the next cohort of innovators</li>\r\n 	<li>Find exact matches for your or client\'s needs</li>\r\n 	<li>Uncover the networks in which prospective candidates are active</li>\r\n 	<li>Expand or supplement candidates’ LinkedIn biographies</li>\r\n</ul>\r\n<strong>ipQuants Value:</strong>\r\n<div class=\"quote quote_3\">\r\n\r\n<i>“Researchers and sourcers, in particular, will find ipQuants especially useful when initially analyzing a market and building a list of potential candidates. Consultants who also conduct their own business development activities will find a lot of value.”</i>\r\n<div class=\"quote__author\">\r\n\r\nJacob Williamson\r\n\r\nResearch Assistant\r\n\r\nAdamson &amp; Partners\r\n\r\n</div>\r\n</div>', 'Talent Sourcing', '', 'publish', 'closed', 'closed', '', 'talent-sourcing', '', '', '2018-05-02 13:45:29', '2018-05-02 13:45:29', '', 0, 'https://localhost/ipquants/dist/?post_type=industries&#038;p=91', 0, 'industries', '', 0);
INSERT INTO `ipqu_posts` VALUES (92, 1, '2018-05-02 13:45:44', '2018-05-02 13:45:44', '<strong>Titles:</strong> Senior Partner, Chief Marketing Officer, Business Development\r\n\r\n<strong>Use Cases:</strong>\r\n<ul>\r\n 	<li>Competitive research tool to know which law firms are most active and competitive in specific categories</li>\r\n 	<li>Business development tool to find sales leads and opportunities</li>\r\n 	<li>Conflict of interest checker</li>\r\n 	<li>European and US patent law firm data</li>\r\n 	<li>Questions answered in minutes rather than weeks</li>\r\n</ul>\r\n<strong>ipQuants Value:</strong>\r\n<div class=\"quote quote_3\">\r\n\r\n<i>“ipQuants help you know where you stand on the innovation landscape and the environment in which you operate. You can understand the landscape in minutes then create a report and share it with a partner or client. It’s especially helpful for mid-size law firms that lack the large research teams of much larger firms.”</i>\r\n<div class=\"quote__author\">\r\n\r\nSteve Saunders\r\n\r\nPartner and Vice Chair of the Patent Practice Group\r\n\r\nSunstein Kann Murphy &amp; Timbers LLP\r\n\r\n</div>\r\n</div>', 'Law Firm Management', '', 'publish', 'closed', 'closed', '', 'law-firm-management', '', '', '2018-05-02 13:45:44', '2018-05-02 13:45:44', '', 0, 'https://localhost/ipquants/dist/?post_type=industries&#038;p=92', 0, 'industries', '', 0);
INSERT INTO `ipqu_posts` VALUES (93, 1, '2018-05-02 13:45:58', '2018-05-02 13:45:58', '<strong>Titles:</strong> Director, Mergers &amp; Acquisitions; Business Analyst, Consultant, M&amp;A; Venture Capitalist\r\n\r\n<strong>Use Cases:</strong>\r\n<ul>\r\n 	<li>Get a basic understanding of filing trends</li>\r\n 	<li>Quickly check up on inventors and tech sectors</li>\r\n 	<li>Identify open innovation partners within a specific tech field</li>\r\n 	<li>A strategy tool that creates a profile or overview of a particular company</li>\r\n 	<li>Enables you to get back to people either internally or externally very quickly</li>\r\n</ul>\r\n<strong>ipQuants Value:</strong>\r\n<div class=\"quote quote_3\">\r\n\r\n<i>“ipQuants is particularly useful for anyone doing M&amp;A research and due diligence as well as early-stage investing. It helps you learn not only what is valuable but what has potential.”</i>\r\n<div class=\"quote__author\">\r\n\r\nJack Aksan\r\n\r\nCEO and Founder\r\n\r\nCatey GmbH\r\n\r\n</div>\r\n</div>', 'Investment Analysis', '', 'publish', 'closed', 'closed', '', 'investment-analysis', '', '', '2018-05-02 13:45:58', '2018-05-02 13:45:58', '', 0, 'https://localhost/ipquants/dist/?post_type=industries&#038;p=93', 0, 'industries', '', 0);
INSERT INTO `ipqu_posts` VALUES (94, 1, '2018-05-02 13:46:14', '2018-05-02 13:46:14', '<strong>Titles:</strong> Strategic IP Counsel, Chief IP Counsel\r\n\r\n<strong>Use Cases:</strong>\r\n<ul>\r\n 	<li>Quickly understand a tech field, player, domain or company</li>\r\n 	<li>Identify your main competitors and their top innovators</li>\r\n 	<li>Understand which institutions are strong in a tech field</li>\r\n 	<li>Identify a network trail rather than a single data point</li>\r\n 	<li>Drill down into tech domains to the relevant technology classification</li>\r\n 	<li>Identify frequently cited companies and inventors</li>\r\n 	<li>Identify open innovation partners within a particular field</li>\r\n</ul>\r\n<strong>ipQuants Value:</strong>\r\n<div class=\"quote quote_3\">\r\n\r\n<i>“In our business, you have to be a curious guy. I can go into ipQuants and in minutes, I have a really good idea which academic institutions are strong, who the top inventors are and which companies are working in the same fields.”</i>\r\n<div class=\"quote__author\">\r\n\r\nChief IP Counsel\r\n\r\nMedTech Manufacturer\r\n\r\n</div>\r\n</div>', 'Strategic IP Management', '', 'publish', 'closed', 'closed', '', 'strategic-ip-management', '', '', '2018-05-02 13:46:14', '2018-05-02 13:46:14', '', 0, 'https://localhost/ipquants/dist/?post_type=industries&#038;p=94', 0, 'industries', '', 0);
